package mpt

import (
	"encoding/json"
	"testing"

	"github.com/joeqian10/neo-gogogo/helper"
	"github.com/joeqian10/neo-gogogo/helper/io"
)

func TestStateRootSerialize(t *testing.T) {
	var sr StateRoot
	data := []byte(`{"version":0,"index":19571,"prehash":"0xf4e4a59b1c89dd3343f18d47bb12429b04cb5d179af680e8337240cd2f427de0","stateroot":"0x34db5a993a95e0db79efe8220bf142e5952056bb59834fe3b91fc1611ed4385e","witness":[{"invocation":"","verification":"53210244d12fa1b7a9a2cd3c15c4f2058154510dbc4ec42aba2dca216be68f1689c3f3210394f30f5a77ee3096a66ebaf58b97db17b9b51c692bda171a6e459de67c118d7a210397b563c76bcf493939e234caf40bca1ac9e2edd29973dccdd11def8713e708ec2102f257d51b7966eb2b11cd4d8644bc55dd7a39293f9c027a3cede5ae0289c2e8e354ae"}]}`)
	err := json.Unmarshal(data, &sr)
	if err != nil {
		t.Error(err)
	}
	data, _ = io.ToArray(&sr)
	if helper.BytesToHex(data) != "" {
		t.Error("serialize failed")
	}
	io.AsSerializable(&sr, data)
	data, _ = io.ToArray(&sr)
	if helper.BytesToHex(data) != "" {
		t.Error("serialize failed")
	}
}
